mapper.update:
update member_detail
birth=#{birth}, phone=#{phone}, gender=#{gender}, addr1=#{addr1}, addr2=#{addr2}
where id=#{id}

mapper.select:

    		select * from (select b.* , rownum r from
			    (select * from allcoupon) b)
				where r >=#{start} and r <=#{end}

    select * from productinfo
	    WHERE SUBSTR(productid, 3, 2) IN 

	        #{item}

	    AND kcal between #{minkcal} and #{maxkcal}
